package com.perscholas.java_basics;

/*Write a program that declares 2 double variables, assigns a number to each, and adds them together. Assign the sum to a variable. Print out the result.*/

public class JavaBasicsClass_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double firstVariable= 10.0;
		double secondVariable= 10.0;
		
		double sum= firstVariable+secondVariable;
		
		System.out.println("The sum of " + firstVariable +" and " + secondVariable +" is: " + sum);
	}

}
